DROP DATABASE IF EXISTS xeno;CREATE DATABASE IF NOT EXISTS xeno;CREATE TABLE `xeno`.`users` (`id` int UNSIGNED NOT NULL AUTO_INCREMENT,`first_name` tinytext CHARACTER SET utf8 COLLATE utf8_unicode_ci NOT NULL,`last_name` tinytext CHARACTER SET utf8 COLLATE utf8_unicode_ci NOT NULL,`date_joined` datetime NOT NULL DEFAULT CURRENT_TIMESTAMP(),`credits` int NOT NULL DEFAULT 100,`suspended_until` datetime NOT NULL,`acct_type` int UNSIGNED NOT NULL DEFAULT 3,`userid` varchar(50) CHARACTER SET utf8 COLLATE utf8_unicode_ci NOT NULL,`hpass` varchar(128) CHARACTER SET utf8 COLLATE utf8_unicode_ci NOT NULL,UNIQUE INDEX `id` (`id`),UNIQUE INDEX `userid` (`userid`))ENGINE=InnoDBDEFAULT CHARACTER SET=utf8 COLLATE=utf8_unicode_ci;CREATE TABLE `xeno`.`acct_types` (`id` int UNSIGNED NOT NULL AUTO_INCREMENT,`description` tinytext NOT NULL,UNIQUE INDEX `id` (`id`))ENGINE=InnoDBDEFAULT CHARACTER SET=utf8 COLLATE=utf8_unicode_ci;CREATE TABLE `xeno`.`make` (`id` int UNSIGNED NOT NULL AUTO_INCREMENT,`description` tinytext NOT NULL,UNIQUE INDEX `id` (`id`))ENGINE=InnoDBDEFAULT CHARACTER SET=utf8 COLLATE=utf8_unicode_ci;CREATE TABLE `xeno`.`model` (`id` int UNSIGNED NOT NULL AUTO_INCREMENT,`description` tinytext NOT NULL,UNIQUE INDEX `id` (`id`))ENGINE=InnoDBDEFAULT CHARACTER SET=utf8 COLLATE=utf8_unicode_ci;CREATE TABLE `xeno`.`cars` (`id` int UNSIGNED NOT NULL AUTO_INCREMENT,`make` int UNSIGNED NOT NULL,`model` int UNSIGNED NOT NULL,`year` int NOT NULL,`country` int UNSIGNED NULL DEFAULT 0,`type` int UNSIGNED NOT NULL,`hp` int NOT NULL,`torque` int NOT NULL,`miles_driven` int NOT NULL,`status` int UNSIGNED NOT NULL DEFAULT 1,`added_by` int UNSIGNED NOT NULL,`acceleration` double NULL,`max_speed` int NULL COMMENT 'miles per hour',`is_featured` int UNSIGNED NULL DEFAULT 0,`date_added` datetime NOT NULL DEFAULT CURRENT_TIMESTAMP(),UNIQUE INDEX `id` (`id`))ENGINE=InnoDBDEFAULT CHARACTER SET=utf8 COLLATE=utf8_unicode_ci;CREATE TABLE `xeno`.`car_status` (`id` int UNSIGNED NOT NULL AUTO_INCREMENT,`description` tinytext NOT NULL,UNIQUE INDEX `id` (`id`))ENGINE=InnoDBDEFAULT CHARACTER SET=utf8 COLLATE=utf8_unicode_ci;CREATE TABLE `xeno`.`country` (`id` int UNSIGNED NOT NULL AUTO_INCREMENT,`description` tinytext NOT NULL,UNIQUE INDEX `id` (`id`))ENGINE=InnoDBDEFAULT CHARACTER SET=utf8 COLLATE=utf8_unicode_ci;CREATE TABLE `xeno`.`maintenance_log` (`id` int UNSIGNED NOT NULL AUTO_INCREMENT,`description` text NOT NULL,`date_entered` datetime NOT NULL DEFAULT CURRENT_TIMESTAMP(),`date_resolved` datetime NULL DEFAULT NULL,`car` int UNSIGNED NOT NULL,`maintenance_worker` int UNSIGNED NOT NULL,`last_driver` int UNSIGNED NOT NULL,UNIQUE INDEX `id` (`id`))ENGINE=InnoDBDEFAULT CHARACTER SET=utf8 COLLATE=utf8_unicode_ci;CREATE TABLE `xeno`.`car_type` (`id` int UNSIGNED NOT NULL AUTO_INCREMENT,`description` tinytext NOT NULL,UNIQUE INDEX `id` (`id`))ENGINE=InnoDBDEFAULT CHARACTER SET=utf8 COLLATE=utf8_unicode_ci;CREATE TABLE `xeno`.`reservations` (`id` int UNSIGNED NOT NULL AUTO_INCREMENT,`made_by` int UNSIGNED NOT NULL,`for_car` int UNSIGNED NOT NULL,`for_date` date NOT NULL,`car_returned` tinyint NOT NULL DEFAULT 0 COMMENT '0 for not yet, 1 for yes, 2 for cleared for another rental',`return_time` date NOT NULL DEFAULT 0,UNIQUE INDEX `id` (`id`))ENGINE=InnoDBDEFAULT CHARACTER SET=utf8 COLLATE=utf8_unicode_ci;CREATE TABLE `xeno`.`reviews` (`id` int UNSIGNED NOT NULL AUTO_INCREMENT,`date_created` datetime NOT NULL,`num_stars` tinyint NOT NULL,`text` text CHARACTER SET utf8 COLLATE utf8_unicode_ci NOT NULL,`reviewer` int UNSIGNED NOT NULL,`car` int UNSIGNED NOT NULL,UNIQUE INDEX `id` (`id`))ENGINE=InnoDBDEFAULT CHARACTER SET=utf8 COLLATE=utf8_unicode_ci;CREATE TABLE `xeno`.`waiting_queue` (`id` int UNSIGNED NOT NULL AUTO_INCREMENT,`user` int UNSIGNED NOT NULL,UNIQUE INDEX `id` (`id`))ENGINE=InnoDBDEFAULT CHARACTER SET=utf8 COLLATE=utf8_unicode_ci;ALTER TABLE `xeno`.`users` ADD CONSTRAINT `acct_type` FOREIGN KEY (`acct_type`) REFERENCES `xeno`.`acct_types` (`id`) ON DELETE CASCADE ON UPDATE CASCADE;ALTER TABLE `xeno`.`cars` ADD CONSTRAINT `car_make` FOREIGN KEY (`make`) REFERENCES `xeno`.`make` (`id`) ON DELETE CASCADE ON UPDATE CASCADE;ALTER TABLE `xeno`.`cars` ADD CONSTRAINT `car_model` FOREIGN KEY (`model`) REFERENCES `xeno`.`model` (`id`) ON DELETE CASCADE ON UPDATE CASCADE;ALTER TABLE `xeno`.`cars` ADD CONSTRAINT `car_status` FOREIGN KEY (`status`) REFERENCES `xeno`.`car_status` (`id`) ON DELETE CASCADE ON UPDATE CASCADE;ALTER TABLE `xeno`.`cars` ADD CONSTRAINT `car_country` FOREIGN KEY (`country`) REFERENCES `xeno`.`country` (`id`) ON DELETE CASCADE ON UPDATE CASCADE;ALTER TABLE `xeno`.`maintenance_log` ADD CONSTRAINT `car_checked` FOREIGN KEY (`car`) REFERENCES `xeno`.`cars` (`id`) ON DELETE CASCADE ON UPDATE CASCADE;ALTER TABLE `xeno`.`maintenance_log` ADD CONSTRAINT `maintenance_worker` FOREIGN KEY (`maintenance_worker`) REFERENCES `xeno`.`users` (`id`) ON DELETE CASCADE ON UPDATE CASCADE;ALTER TABLE `xeno`.`maintenance_log` ADD CONSTRAINT `last_driver` FOREIGN KEY (`last_driver`) REFERENCES `xeno`.`users` (`id`) ON DELETE CASCADE ON UPDATE CASCADE;ALTER TABLE `xeno`.`cars` ADD CONSTRAINT `added_by` FOREIGN KEY (`added_by`) REFERENCES `xeno`.`users` (`id`) ON DELETE CASCADE ON UPDATE CASCADE;ALTER TABLE `xeno`.`reservations` ADD CONSTRAINT `made_by` FOREIGN KEY (`made_by`) REFERENCES `xeno`.`users` (`id`) ON DELETE CASCADE ON UPDATE CASCADE;ALTER TABLE `xeno`.`reviews` ADD CONSTRAINT `reviewer` FOREIGN KEY (`reviewer`) REFERENCES `xeno`.`users` (`id`) ON DELETE CASCADE ON UPDATE CASCADE;ALTER TABLE `xeno`.`reviews` ADD CONSTRAINT `car_reviewed` FOREIGN KEY (`car`) REFERENCES `xeno`.`cars` (`id`) ON DELETE CASCADE ON UPDATE CASCADE;ALTER TABLE `xeno`.`cars` ADD CONSTRAINT `car_type` FOREIGN KEY (`type`) REFERENCES `xeno`.`car_type` (`id`) ON DELETE CASCADE ON UPDATE CASCADE;ALTER TABLE `xeno`.`waiting_queue` ADD CONSTRAINT `user_in_queue` FOREIGN KEY (`user`) REFERENCES `xeno`.`users` (`id`) ON DELETE CASCADE ON UPDATE CASCADE;ALTER TABLE `xeno`.`reservations` ADD CONSTRAINT `car_request` FOREIGN KEY (`for_car`) REFERENCES `xeno`.`cars` (`id`) ON DELETE CASCADE ON UPDATE CASCADE;